import React, { Component } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'

export default class CreateNote extends Component {

    state = {
        listusers: [],
        userSelected: '',
        date: new Date()
    }

    componentDidMount() {
        this.getListUsers();
    }

    getListUsers = async () => {
        const users = await axios.get('http://localhost:4000/api/users/');

        this.setState({ listusers: users.data.map(user => user.username) });

        console.log(this.state.listusers);


    }



    enviarInfo = (e) => {
        e.preventDefault();
    }

    onChangeUser = (e) => {
        this.setState({ userSelected: e.target.value });
    }



    render() {
        return (
            <div className="col-md-6 offset-md-3">
                <div className="card card-body">
                    <h4>Crear una tarea</h4>
                    <div className="form-group">
                        <select className="form-control"
                            name="usuarioSeleccionado"
                            onChange={this.onChangeUser}
                        >

                            {
                                this.state.listusers.map(user =>

                                    <option key={user}>{user}</option>

                                )
                            }

                        </select>
                    </div>

                    
                    <form onSubmit={this.enviarInfo}>
                    <div className="form-group">
                        <input type="text" 
                        className="form-control"
                        placeholder="Título"
                        name="title"
                        required
                        />
                    </div>
                    <div className="form-group">
                        <textarea type="text" 
                        className="form-control"
                        placeholder="Descripción"
                        name="description"
                        
                        ></textarea>

                        
                    </div>

                    <div className="form-group">
                        <DatePicker 
                            className="form-control"
                            selected
                        />
                    </div>
                        <button type="submit" className="btn btn-primary">
                            Guardar
                    </button>
                    </form>
                </div>
            </div>
        )
    }
}
